import React, { useState } from 'react';
import { Link } from "@reach/router";

function ResponsiveNavigation({ background, hoverBackground, linkColor, navLinks, logo}){

    const [ navOpen, setNavOpen] = useState(0);
    const [ hoverIndex, setHoverIndex ] = useState(-1);
    const [ responsiveToolBar, setToolBar] = useState(0);


    return (

        <nav 
            className='responsive-toolbar'
            // class={responsiveToolBar ? 'active' : ''}


            style = {{ background: background}}>

                <ul 
                style = {{background: background}}
                className = {navOpen ? 'active' : ''}
                >

                {/* onClick={ () => { setNavOpen(!navOpen) } } */}
                <figure className="image-logo" >
                    <img src={ logo } height="40px" width="40px" alt="toolbar-logo" />
                </figure>
                onClick={ () => {setToolBar(!responsiveToolBar)}}  

            { navLinks.map((link, index) =>
                <li 
                    key = { index }
                    onMouseEnter = { () => { setHoverIndex(index) } }
                    onMouseLeave = { () => { setHoverIndex(-1) } }
                    style = { { background: hoverIndex === index ? (hoverBackground || '#999') : '' }}
                    >
                        <Link 
                            to = { link.path }
                            style = {{ color: linkColor}}
                            >
                                { link.text }
                                <i className= { link.icon } />

                            </Link>

                    </li>
            )}
            </ul>
        </nav>
    )
}

export default ResponsiveNavigation
