import React, { Component } from 'react';
class Counter extends Component {
   
    state = {
        count : 1
    };

    styles = {
        fontSize : 18,
        fontWeight: "bold"
    }

    // classExtract() {
    //     let classes = "badge m-2 badge-";
    //     classes += (this.state.count === 0) ? "warning" : "primary";
    //     return classes;
    // }
    
    functionTest () {
        this.setState((prevState, {count}) => ({
            count : prevState.count + 1
        }));
    };

    render() { 
        return (
            <React.Fragment>
                <button className = "btn btn-secondary btn-sm" onClick = {this.functionTest}>{this.state.count}</button>
            </React.Fragment>
        );
    };
};

 
export default Counter;
